        public Utility(double power, double price)
        {

            if (price < 0)
                throw new ArgumentOutOfRangeException("Cena ne sme biti negativna!");
            else
            {
                Power = power;
                Price = price;
            }
           
        }

        ~Utility()
        {

        }

        /// 
